&#123;
	printf(&quot;%p  &quot;, &amp;g_nNum1);
	printf(&quot;%p\n&quot;, &amp;g_nNum2);
	HANDLE hThread &#x3D; CreateThread(NULL, 0, t_ThreadFun, NULL, 0, 0);
