	*a &#x3D; *b;
	*b &#x3D; temp;
&#125;

&#x2F;&#x2F;3. 引用传递
void mySwap03(int&amp; a, int&amp; b) &#123;
	int temp &#x3D; a;
	a &#x3D; b;
	b &#x3D; temp;
&#125;

int main() &#123;

	int a &#x3D; 10;
	int b &#x3D; 20;

	mySwap01(a, b);
	cout &lt;&lt; &quot;a:&quot; &lt;&lt; a &lt;&lt; &quot; b:&quot; &lt;&lt; b &lt;&lt; endl;

