	&#x2F;&#x2F;冲泡
	virtual void Brew() &#123;
		cout &lt;&lt; &quot;冲泡咖啡!&quot; &lt;&lt; endl;
	&#125;
	&#x2F;&#x2F;倒入杯中
	virtual void PourInCup() &#123;
