	&#x2F;&#x2F; 1.3 检测进程句柄
	if (hProcess &#x3D;&#x3D; INVALID_HANDLE_VALUE)
		return -1;

	&#x2F;&#x2F; 2.1 计算dll路径长度
	DWORD dwSize &#x3D; strlen(DLLPATH) + 1;
	&#x2F;&#x2F; 2.在目标进程申请一段空间
	LPVOID lpAddr &#x3D; VirtualAllocEx(
		hProcess, 0, dwSize, MEM_COMMIT, PAGE_READWRITE);

	&#x2F;&#x2F; 3.1 写入之前可以先测试加载一下看能否成功
	&#x2F;*HMODULE hMod &#x3D; LoadLibraryA(DLLPATH);
