FARPROC WINAPI CAPIHook::GetProcAddress(HMODULE hModule, PCSTR pszProcName)
&#123;
       &#x2F;&#x2F;得到函数的真实地址
       FARPROC pfn &#x3D; ::GetProcAddress(hModule, pszProcName);
       &#x2F;&#x2F;看它是否我们要HOOK的函数
       CAPIHook *p &#x3D; sm_pHeader;
       while(p !&#x3D; NULL)
       &#123;
              if (p-&gt;m_pfnOrig &#x3D;&#x3D; pfn)
              &#123;
                     pfn &#x3D; p-&gt;m_pfnHook;
                     break;
              &#125;
              p &#x3D; p-&gt;m_pNext;
       &#125;
       return pfn;
&#125;
